import React,{ Fragment } from 'react';
import {about,about_update,updatebutton} from '../Styles/About.module.scss';
import IconButton from '@material-ui/core/IconButton';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import {blueGrey} from '@material-ui/core/colors';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

const useStyles = theme => ({
    button:{
      backgroundColor:blueGrey[300],
    },
  });

  class About extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          isToggleOn: false,
          display: 'none',
        };
        this.fileInputEl = React.createRef();
        this.handleClick = this.handleClick.bind(this);
        this.saveClick= this.saveClick.bind(this);
        this.cancelClick= this.saveClick.bind(this);
      }
    render(){
        const { classes } = this.props;
    return ( 
        <Fragment>
        <div className={about}>
            <div>ABOUT</div>
            <div>Edit your description now.</div>
            <IconButton className={classes.button+" "+updatebutton}>
                <CreateOutlinedIcon />
            </IconButton>
        </div>
        <div 
        className={about_update} 
        style={{display: this.state.display}}>    
            <div className={classes.upload_button}>
            <TextField id="standard-basic" label="Name" onChange={this.handleOnChange} />
            <Button variant="contained" onClick={this.handleClick}>
            Cancel
            </Button>
            <Button variant="contained" onClick={this.saveClick}>
                Save
            </Button>
            </div>
        </div>
    </Fragment>
      );
    }

  }

export default withStyles(useStyles)(About);
