import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import { deepOrange,blueGrey} from '@material-ui/core/colors';
import {background_container,avatar,updatebutton} from '../Styles/Background.module.scss';
import IconButton from '@material-ui/core/IconButton';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import { storage } from "./firebase/firebase";

const useStyles = theme => ({
    default: {
      fontSize:theme.spacing(7),
      color: theme.palette.getContrastText(deepOrange[500]),
      backgroundColor: deepOrange[500],
      width: theme.spacing(10),
      height: theme.spacing(10),
    },
    button:{
      backgroundColor:blueGrey[300],
    },
  });


class Background extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      image: null,
      path: null,
      url: "",
      users: [],
    };
  }


  onFileChange = (event) => {
    if (event.target.files[0]) {
      const image = event.target.files[0];
      this.setState({
        image: image,
        path: URL.createObjectURL(event.target.files[0]),
      });
    }
  };

  uploadFile = (event) => {
    event.preventDefault();
    console.log(this.state);
    const { image } = this.state;
    //const uploadTask = storage.ref(`images/${image.name}`).put(image);
    //uploadTask.on('state_changed', progress, error,complete)
    if (image === null) {
      console.error(`not an image, should be ${typeof image}`);
    }

    const uploadTask = storage.ref(`/images/${image.name}`).put(image);

    uploadTask.on(
      "state_changed",
      (snapShot) => {
        //takes a snap shot of the process as it is happening
        //console.log(snapShot);
      },
      (err) => {
        //catches the errors
        console.log(err);
      },
      () => {
        // gets the functions from storage refences the image storage in firebase by the children
        // gets the download url then sets the image from firebase as the value for the imgUrl key:
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then((url) => {
            console.log(url);
            this.setState({ url });
          });
      }
    );
  };

  componentDidMount() {//网络请求都是放在此生命周期函数中
      fetch( 'http://localhost:8090/users')
      .then(response => response.json())
      .then(data =>{
        console.log(data)
        this.setState({totalData: data.arr})  ////赋值到本地数据
        console.log(this.state.totalData)
      })
      .catch(e => console.log('错误:', e))       
  }
    



  render(){
    const { classes } = this.props;
  return (
    <div className={background_container}>
      <div className="main">
                    
        { 
            this.state.users.map((item,index)=>{
              return <p key={index}>{item.userName}</p>
          })
        }
      
      </div>
      <input onChange={this.onFileChange} type="file"></input>
      <Avatar alt="defaut" src={this.state.url} className={classes.default+" "+avatar}>
        q
      </Avatar>
      <IconButton onClick={this.uploadFile} className={classes.button+" "+updatebutton}>
        <CreateOutlinedIcon />
      </IconButton>
    </div>  
  );
  }}


export default withStyles(useStyles)(Background);
