import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import {blueGrey} from '@material-ui/core/colors';
import SimpleTabs from './SimpleTabs'

const useStyles = theme => ({
  root: {
    flexGrow: 1,
  },
  location:{
      color:blueGrey[300],
      width:15,
      height:15,
  }
});


class Basicinfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          users: [],
        };
      }

      componentDidMount() {//网络请求都是放在此生命周期函数中
        fetch( 'http://localhost:8090/users/1')
        .then(response => response.json())
        .then(data =>{
          console.log(data)
          this.setState({users: data})  ////赋值到本地数据
          console.log(this.state.users)
        })
        .catch(e => console.log('错误:', e))       
    }
      
    render(){
    const { classes } = this.props;
    return (
            <div className={classes.root}>
                <Grid container spacing={3}>
                <Grid item xs={6}>
                    <div className="username">
                        {this.state.users.userName}
                    </div>               
                    <div className="last_sign_in">
                        Last online 13 hours ago
                    </div>
                    <div className="location">
                        <LocationOnIcon  className={classes.location}/>
                        {this.state.users.location}
                    </div>
                    <div className="start_date">
                        Member since 8th Jun 2020
                    </div>
                </Grid>
                <Grid item xs={6}>
                    <SimpleTabs />
                </Grid>
                </Grid>
            </div>      
    );
    }
  }

export default withStyles(useStyles)(Basicinfo);
