import React, { Fragment } from 'react';
import { withStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import { deepOrange,blueGrey} from '@material-ui/core/colors';
import {cloud_upload,info_update,background_container,avatar,updatebutton} from '../Styles/Background.module.scss';
import IconButton from '@material-ui/core/IconButton';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import { storage } from "./firebase/firebase";

const useStyles = theme => ({
    default: {
      fontSize:theme.spacing(7),
      color: theme.palette.getContrastText(deepOrange[500]),
      backgroundColor: deepOrange[500],
      width: theme.spacing(10),
      height: theme.spacing(10),
    },
    button:{
      backgroundColor:blueGrey[300],
    },
    update_button:{
      backgroundColor:"white",
    }
  });


class Background extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      image: null,
      path: null,
      url: "",
      isToggleOn: false,
      display: 'none',

    };
    this.fileInputEl = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }
  
  
 
  handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn,
      display: prevState.isToggleOn ? 'none': 'block'
    }));
  }



  onFileChange = (event) => {
    if (event.target.files[0]) {
      const image_file = event.target.files[0];
      this.setState({
        image: image_file,
        path: URL.createObjectURL(event.target.files[0]),
      });
      this.setState(prevState => ({
        isToggleOn: !prevState.isToggleOn,
        display: prevState.isToggleOn ? 'none': 'block'
      }));
      console.log(this.state);
    const { image } = this.state;
    //const uploadTask = storage.ref(`images/${image.name}`).put(image);
    //uploadTask.on('state_changed', progress, error,complete)
    if (image === null) {
      console.error(`not an image, should be ${typeof image}`);
    }

  const uploadTask = storage.ref(`/images/${image.name}`).put(image);

  uploadTask.on(
    "state_changed",
    (snapShot) => {
      //takes a snap shot of the process as it is happening
      //console.log(snapShot);
    },
    (err) => {
      //catches the errors
      console.log(err);
    },
    () => {
      // gets the functions from storage refences the image storage in firebase by the children
      // gets the download url then sets the image from firebase as the value for the imgUrl key:
      storage
        .ref("images")
        .child(image.name)
        .getDownloadURL()
        .then((url) => {
          console.log(url);
          this.setState({ url });
        });
    }
  );
    }
  };

  uploadFile = (event) => {
    event.preventDefault();
    console.log(this.state);
    const { image } = this.state;
    //const uploadTask = storage.ref(`images/${image.name}`).put(image);
    //uploadTask.on('state_changed', progress, error,complete)
    if (image === null) {
      console.error(`not an image, should be ${typeof image}`);
    }

  const uploadTask = storage.ref(`/images/${image.name}`).put(image);

  uploadTask.on(
    "state_changed",
    (snapShot) => {
      //takes a snap shot of the process as it is happening
      //console.log(snapShot);
    },
    (err) => {
      //catches the errors
      console.log(err);
    },
    () => {
      // gets the functions from storage refences the image storage in firebase by the children
      // gets the download url then sets the image from firebase as the value for the imgUrl key:
      storage
        .ref("images")
        .child(image.name)
        .getDownloadURL()
        .then((url) => {
          console.log(url);
          this.setState({ url });
        });
    }
  );
};

    
  render(){
    const { classes } = this.props;
  return (
    <Fragment>
    <div className={background_container}>
      <input 			
        type="file"
        ref={this.fileInputEl}	//挂载ref
        accept=".jpg,.jpeg,.png"	//限制文件类型
        hidden	//隐藏input 
        onChange={this.onFileChange} >
      </input>
      <Avatar alt="defaut" src={this.state.url} className={classes.default+" "+avatar}>
        q
      </Avatar>
      <IconButton onClick={this.handleClick} className={classes.button+" "+updatebutton}>
        <CreateOutlinedIcon />
      </IconButton>
      <IconButton onClick={() => {
        this.fileInputEl.current.click()		//当点击a标签的时候触发事件
      }} className={classes.update_button+" "+cloud_upload} style={{display: this.state.display}}>
      <CloudUploadIcon  />
      </IconButton>
      <IconButton 
        onClick={this.uploadFile} >
        <CreateOutlinedIcon />
      </IconButton>    
    </div>
    <div 
      className={info_update} 
      style={{display: this.state.display}}>
    </div> 
    </Fragment>
  );
  }}


export default withStyles(useStyles)(Background);
